import React, { useState } from 'react';
import Alert from 'react-bootstrap/Alert';

/*
    기본 알림 + 삭제 기능 제공
*/

const Alerts = () =>{
    return(
        <Alert variant="danger" dismissible>
            <Alert.Heading>에러가 발생했습니다!</Alert.Heading>
            <p>x 버튼을 누를 시 알림 메시지가 삭제됩니다.</p>
        </Alert>
    );
}

export default Alerts
