import React, { useState, Fragment } from 'react';

const Counter = () => {
  const [size, setSize] = useState({ width: 200, height: 100 });
  const [inputWidth, setInputWidth] = useState('');

  const handleChange = (event) => {
    setInputWidth(event.target.value);
  };

  const handleWidthChange = () => {
    // 입력 값이 유효한지 확인하고 유효한 경우에만 너비를 변경합니다.
    const newWidth = parseInt(inputWidth);
    if (!isNaN(newWidth)) {
      setSize({ ...size, width: newWidth });
      setInputWidth(''); // 입력 필드 초기화
    } else {
      alert('유효한 숫자를 입력하세요.');
    }
  };

  return (
    <Fragment>
      <h1>너비: {size.width}, 높이: {size.height}</h1>
      <input
        type="text"
        value={inputWidth}
        onChange={handleChange}
      />
      <button onClick={handleWidthChange}>너비 변경</button>
    </Fragment>
  );
};

export default Counter;
