import {Header, Nav, Article} from './main.js'
import { useState } from 'react';
import './App.css'

const topics =  [
  {id: 1,title : "html", body: "html is ...", img: "html.png"},
  {id: 2,title : "css", body: "css is ...", img: "css.png"},
  {id: 3,title : "javascript", body: "js is ...", img: "js.png"}
];


const App = ()=>{
  const [id, setId] = useState(null);
  const [mode, setMode] = useState("Welcome");
  let img = null;

  if(mode === "Welcome"){
    img = require('./img/web.png');
  }
  else if(mode === "Read"){
      topics.map((topic)=>{
          console.log(topic.id);
        }
      )
  }

  return(
    <>
      <Header onChangeMode={()=>
        setMode("Welcome")
      }/>
      <Nav title={topics} onChangeMode={(id)=>{
        setMode("Read");
        setId(id);
      }}/>
      <div className='wrapper'>
        <img src={img}/>
        <Article title="Welcom" body="Hello, WEB"/>
      </div>
    </>
  );
}

export default App;
