const appStyle = {
  textAlign: "left"
};

const headerStyle = {
  backgroundColor: "black",
  color: "yellow",
  textAlign: "center"
};

const titleStyle = {
  textDecoration: "underline",
  textShadow: "4px 1px #9c9c9c"
};

const App = () => {
  return (
    <div style={appStyle}>
      <header style={headerStyle}>
        <h1 style={titleStyle}>Hello, React!</h1>
      </header>
      <p>React 스타일링 수업에 잘 오셨습니다!</p>
    </div>
  );
};

export default App;
