import React, {useEffect, useState} from "react";

const App = () =>{
    const [count, setCount] = useState(1);
    const [name, setName] = useState('');

    const handelUpdate = () =>{
        setCount(count+1);
    }

    const handelInputChange = (e) =>{
        setName(e.target.value);
    }

    // 렌더링 될 때마다 실행
    useEffect(()=>{
        console.log("렌더링");
    })
    return(
        <>
        <button onClick={handelUpdate}>Update</button>
        <p>count: {count}</p>
        <input text="type" value={name} onChange={handelInputChange}/>
        <span>name : {name}</span>
        </>
    );
}

export default App;
