import './App.css';

const Nav = ({topics})=>{
  const topicList = topics.map((topic)=>
    <li key={topic.id}>
      <a href={'/read/'+topic.id} onClick={(event)=>{
        event.preventDefault();
        alert(topic.id);
      }}>
        {topic.title}
      </a>
    </li>
  );

  return(
    <nav>
      <ol>
        {topicList}
      </ol>
    </nav>
  );
}

const Article = ({title, body})=>{
  return(
  <article>
        <h3>{title}</h3>
        {body}
  </article>
  );
}

const Header = ({title, onChangeMode})=>{
  return(
  <header>
    <h1>
      <a href="/title/" onClick={(event)=>{
          event.preventDefault();
          onChangeMode();
        }
      }>{title}</a>
    </h1>
  </header>
  );
}

const App = () => {
  const topics = [
    {id : 1 ,title : "html", body : "html is ..."},
    {id : 2 ,title : "css", body : "css is ..."},
    {id : 3 ,title : "js", body : "js is ..."}
  ]

  return(
    <>
      <Header title="WEB" onChangeMode={()=>{
          alert('Header');
        }
      }/>
      <Nav topics={topics}/>
      <Article title="Welcom" body="Hello, WEB"/>
    </>
  )
};

export default App;
