import React from 'react'
import Alerts from './reactstrap/_Alert'
import './App.css'
import 'bootstrap/dist/css/bootstrap.css'   // bootstrap css 적용

/*
    bootstrap은 프론트엔드 디자인을 쉽게 구현할 수 있도록 도와주는 
    html, css, js 프레임워크다.
    bootstrap을 react에서 사용할 수 있도록 패키지로 만든 것이 reactstrap이다.

    ! 사용 전 패키지 설치 방법 
    npm install -save reactstrap 
    npm install -save bootstrap  
    (reactstrap은 bootstrap css를 포함하고 있지 않기 때문에 따로 설치해야함)

*/
const App = () =>{
    return(
        <div>
            <h1>Start React 200!</h1>
            <p>CSS 적용하기</p>
            <Alerts />
        </div>
    );
}   

export default App;
