import {Header, Nav, Article} from './main.js'
import { useState } from 'react';
import './App.css'

const topics =  [
  {id: 1,title : "html"},
  {id: 2,title : "css"},
  {id: 3,title : "javascript"}
];


const App = ()=>{
  const [mode, setMode] = useState("welcome");
  const img = null;

  if(mode === "Welcome"){
    img = require('./img/web.png');
  }
  else if(mode === "Read")
  return(
    <>
      <Header />
      <Nav title={topics}/>
      <div className='wrapper'>
        <img src={img}/>
        <Article title="Welcom" body="Hello, WEB"/>
      </div>
    </>
  );
}

export default App;
