import React from "react";
import { Badge, Button } from "reactstrap";
/* 
    Badge 패키지는 부모 요소에 추가로 특정 문자열이나 숫자를 표시할 때 사용
    - pill 속성 추가 시 테두리 둥글게 적용 가능
    - href 속성을 사용해 배지에 링크 연결 
*/
const _Badge = () => {
    return(
        <div>
            <h1>PRODUCT NAME <Badge color="secondary">hit</Badge></h1>
            <Button color="light" outline>
                Accessor <Badge color="dark">4</Badge>
            </Button>
            <Badge color="danger" pill>pill</Badge>
            <Badge href="/badge" color="light">GoLink</Badge>
        </div>
    );
};

export default _Badge;
