import React from "react";
import { Alert, UncontrolledAlert } from "reactstrap";


/*
    Alert :  기본 알림 내용 표시
    UncontrolledAlert : Alert + 삭제 기능 추가
*/

const _Alert = () =>{
    return(
        <div>
            <Alert>
                Simple Alert
            </Alert>
            <UncontrolledAlert>
                x 버튼 누르면 알림 삭제
            </UncontrolledAlert>
        </div>
    );
}

export default _Alert;
