import { Fragment, useState } from "react"
const Couter = () =>{
   const [state, setState] = useState(0);

   return(
      <Fragment>
         <h1>state 값: {state}</h1>
         <button onClick={()=>setState(state*2+1)}>홀수씩 더하기</button>
      </Fragment>
   )
};

export default Couter;
