import React from "react";
import { Link } from "react-router-dom";
import { useLocation } from "react-router-dom";

const Nav = ({topics, setTopic}) =>{
    const location = useLocation();
    const onClick = () =>{
        setTopic(topics.filter(topic => topic.link === location.pathname));
    }
    const linkList = topics.map(topic =>(
        <li key={topic.link}>
        <Link to={`/${topic.link}`} onClick={onClick} >{topic.link}</Link>
        </li>
    ));    


    return(
        <nav>
            <ul>
                {linkList}
            </ul>
        </nav>
    );  
}

export default Nav;
