import { useState } from "react"

const Info = ({isLoginIn})=>{
    if(isLoginIn){
        return(
            <p>
                안녕하세요 <u>홍길동</u>님!
            </p>
        );
    }
    else{
        return(
        <p>로그인 후 이용 가능합니다.</p>
        );
    }
}

const Login = () =>{
    const [isLoginIn, setState] = useState(false);

    const clickLogin = () =>{
        setState(true);
    }


    const clickLogout = () =>{
        setState(false);
    }

    let button;
    if(isLoginIn){
        button = <button onClick={clickLogout}>Logout</button>
    }
    else{
        button = <button onClick={clickLogin}>Login</button>
    }

    return(
        <>
        {button}
        <Info isLoginIn = {isLoginIn} />
        </>
    );
}

export default Login;
